mapper.insert:
insert into account_token(
	account_token_no, 
	account_token_target, 
	account_token_value
)
values(
	#{accountTokenNo}, 
	#{accountTokenTarget}, 
	#{accountTokenValue}
)

mapper.select:
select * from account_token
where account_token_target = #{accountTokenTarget}
	and account_token_value = #{accountTokenValue}
